'use strict';

/**
* Compute the least common multiple of all numbers from 1 to `n`.
*
* @module @stdlib/math/base/special/lcmf
*
* @example
* var lcmf = require( '@stdlib/math/base/special/lcmf' );
*
* var v = lcmf( 10 );
* // returns 2520
*
* v = lcmf( 15 );
* // returns 360360
*
* v = lcmf( 0 );
* // returns 0
*
* v = lcmf( NaN );
* // returns NaN
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
