#include "stdlib/math/base/special/lcmf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/gcd.h"

/**
* Computes the least common multiple (lcm) for a single number and a constant.
*
* @param a    integer
* @return     least common multiple (NaN if input is invalid)
*
* @example
* double out = stdlib_base_lcmf( 21.0 );
* // returns 21.0
*/
double stdlib_base_lcmf( const double a ) {
    double abs_a;
    double gcd_value;
    const double b = 1.0;

    if ( a != a ) {
        return a;
    }

    if ( a == 0.0 ) {
        return 0.0;
    }

    abs_a = ( a < 0.0 ) ? -a : a;

	// Note: we rely on `gcd` to perform further argument validation...
    
    gcd_value = stdlib_base_gcd( abs_a, b );

    if ( stdlib_base_is_nan( gcd_value ) ) {
        return gcd_value;
    }

    return ( abs_a / gcd_value ) * b;
}
