'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var lcmf = require( './../lib' );

// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof lcmf, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `NaN` if input is `NaN`', function test( t ) {
	var v;

	v = lcmf( NaN, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, NaN );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( NaN, NaN );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if input is `+infinity`', function test( t ) {
	var v;

	v = lcmf( PINF, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, PINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( PINF, PINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if input is `-infinity`', function test( t ) {
	var v;

	v = lcmf( NINF, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, NINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( NINF, NINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if input is not an floating point number', function test( t ) {
	var v;

	v = lcmf( 3.14, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, 3.14 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 3.14, 6.18 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `0` if input is `0`', function test( t ) {
	var v = lcmf( 0.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 2.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 0.0, -3.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

	t.end();
});

tape( 'the function computes the least common multiple for two single precision floating-point numbers', function test( t ) {
    var v;

    v = lcmf( 0.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 1.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 0.0, 1.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 6.0, 4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( 6.0, -4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( -6.0, -4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( 2.0, 8.0 );
    t.strictEqual( v, 8.0, 'returns 8' );

    v = lcmf( 15.0, 20.0 );
    t.strictEqual( v, 60.0, 'returns 60' );

    v = lcmf( 20.0, 15.0 );
    t.strictEqual( v, 60.0, 'returns 60' );

    v = lcmf( 35.0, -21.0 );
    t.strictEqual( v, 105.0, 'returns 105' );

    v = lcmf( 48.0, 18.0 );
    t.strictEqual( v, 144.0, 'returns 144' );

    t.end();
});