'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var tryRequire = require( '@stdlib/utils/try-require' );

// VARIABLES //

var lcmf = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
    'skip': ( lcmf instanceof Error )
};

// TESTS //

tape( 'main export is a function', opts, function test( t ) {
    t.ok( true, __filename );
    t.strictEqual( typeof lcmf, 'function', 'main export is a function' );
    t.end();
});

tape( 'the function returns `NaN` if either argument is `NaN`', opts, function test( t ) {
    var v;

    v = lcmf( NaN, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, NaN );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( NaN, NaN );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    t.end();
});

tape( 'the function returns `NaN` if either argument is `+infinity`', opts, function test( t ) {
    var v;

    v = lcmf( PINF, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, PINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( PINF, PINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    t.end();
});

tape( 'the function returns `NaN` if either argument is `-infinity`', opts, function test( t ) {
    var v;

    v = lcmf( NINF, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, NINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( NINF, NINF );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    t.end();
});

tape( 'the function returns `NaN` if either argument is not a valid floating-point number', opts, function test( t ) {
    var v;

    v = lcmf( 3.14, 10.0 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 10.0, 3.14 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    v = lcmf( 3.14, 6.18 );
    t.strictEqual( isnan( v ), true, 'returns NaN' );

    t.end();
});

tape( 'the function returns `0` if either argument is `0`', opts, function test( t ) {
    var v = lcmf( 0.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 2.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 0.0, -3.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    t.end();
});

tape( 'the function computes the least common multiple of two floating-point numbers', opts, function test( t ) {
    var v;

    v = lcmf( 0.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 1.0, 0.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 0.0, 1.0 );
    t.strictEqual( v, 0.0, 'returns 0' );

    v = lcmf( 6.0, 4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( 6.0, -4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( -6.0, -4.0 );
    t.strictEqual( v, 12.0, 'returns 12' );

    v = lcmf( 2.0, 8.0 );
    t.strictEqual( v, 8.0, 'returns 8' );

    v = lcmf( 15.0, 20.0 );
    t.strictEqual( v, 60.0, 'returns 60' );

    v = lcmf( 20.0, 15.0 );
    t.strictEqual( v, 60.0, 'returns 60' );

    v = lcmf( 35.0, -21.0 );
    t.strictEqual( v, 105.0, 'returns 105' );

    v = lcmf( 48.0, 18.0 );
    t.strictEqual( v, 144.0, 'returns 144' );

    v = lcmf( 5.0, 6.0 ); // LCM(5, 6) = 30
    t.strictEqual( v, 30.0, 'returns 30' );

    v = lcmf( 3.0, 8.0 ); // LCM(3, 8) = 24
    t.strictEqual( v, 24.0, 'returns 24' );

    t.end();
});
