'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var tryRequire = require( '@stdlib/utils/try-require' );

// VARIABLES //

var lcmf = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( lcmf instanceof Error )
};

// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof lcmf, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `NaN` if the argument is `NaN`', opts, function test( t ) {
	var v;

	v = lcmf( NaN );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if the argument is `+infinity`', opts, function test( t ) {
	var v;

	v = lcmf( PINF );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if the argument is `-infinity`', opts, function test( t ) {
	var v;

	v = lcmf( NINF );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `NaN` if the argument is not an integer', opts, function test( t ) {
	var v;

	v = lcmf( 3.14 );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	v = lcmf( 6.18 );
	t.strictEqual( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `0` if the argument is `0`', opts, function test( t ) {
	var v = lcmf( 0 );
	t.strictEqual( v, 0, 'returns 0' );

	t.end();
});

tape( 'the function computes the least common multiple of a number and a predefined value', opts, function test( t ) {
	var v;

	var predefinedValue = 12;

	v = lcmf( 0 );
	t.strictEqual( v, predefinedValue, 'returns ' + predefinedValue );

	v = lcmf( 6 );
	t.strictEqual( v, predefinedValue, 'returns ' + predefinedValue );

	v = lcmf( 4 );
	t.strictEqual( v, predefinedValue, 'returns ' + predefinedValue );

	v = lcmf( -4 );
	t.strictEqual( v, predefinedValue, 'returns ' + predefinedValue );

	v = lcmf( 15 );
	t.strictEqual( v, 60, 'returns 60' );  // LCM(15, 12) = 60

	v = lcmf( 5 );
	t.strictEqual( v, 60, 'returns 60' );  // LCM(5, 12) = 60

	v = lcmf( 7 );
	t.strictEqual( v, 84, 'returns 84' ); // LCM(7, 12) = 84

	t.end();
});
